import React, { useState } from 'react';
import { Button, TextField, List, ListItem, ListItemText, IconButton } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import CheckCircleOutlineIcon from '@mui/icons-material/CheckCircleOutline';
import RadioButtonUncheckedIcon from '@mui/icons-material/RadioButtonUnchecked';

const ToDoList = ({ title }) => {
  const [tasks, setTasks] = useState([]);
  const [newTask, setNewTask] = useState('');

  const addTask = () => {
    if (!newTask) return;
    setTasks([...tasks, { text: newTask, completed: false }]);
    setNewTask('');
  };

  const toggleTask = index => {
    const newTasks = [...tasks];
    newTasks[index].completed = !newTasks[index].completed;
    setTasks(newTasks);
  };

  const deleteTask = index => {
    const newTasks = tasks.filter((_, i) => i !== index);
    setTasks(newTasks);
  };

  return (
    <div style={{ margin: '20px', padding: '20px' }}>
      <h2>{title}</h2>
      <TextField 
        label="New Task" 
        variant="outlined" 
        fullWidth 
        value={newTask} 
        onChange={e => setNewTask(e.target.value)} 
        style={{ marginBottom: '20px' }}
      />
      <Button variant="contained" color="primary" onClick={addTask} style={{ marginBottom: '20px' }}>Add Task</Button>
      <List>
        {tasks.map((task, index) => (
          <ListItem 
            key={index} 
            secondaryAction={
              <IconButton edge="end" aria-label="delete" onClick={() => deleteTask(index)}>
                <DeleteIcon />
              </IconButton>
            }
          >
            <IconButton onClick={() => toggleTask(index)}>
              {task.completed ? <CheckCircleOutlineIcon /> : <RadioButtonUncheckedIcon />}
            </IconButton>
            <ListItemText 
              primary={task.text} 
              style={{ textDecoration: task.completed ? 'line-through' : 'none' }}
            />
          </ListItem>
        ))}
      </List>
    </div>
  );
};

export default ToDoList;
